import {React, useState} from 'react'
import { FlexCol, PaddingX, Padding, FlexRow } from '../constants/style'
import { Fade, Slide } from 'react-reveal';
import Pulse from 'react-reveal/Pulse';
import { useNavigate } from 'react-router-dom';

export default function Home() {
    const navigate = useNavigate();

    const handleOrderClick = () => {
      // '/order' 경로로 페이지 이동
      navigate('/order');
    };

  return (
    //bg-background에서 background는 tailwind.config.js에서 설정했습니다
    //tailwindcss에 속성을 어떻게 줄 수 있는지는 https://tailwindcss.com/docs/overflow#class-reference 사이트에 있습니다
    //font-Gangwon은 index.css에 정의했습니다
    //Fade 등 위에 import된 효과들은 react-reveal로, 애니메이션 효과입니다
    <div className={`${FlexCol} bg-brown relative z-0 h-screen`}>
      <img src={process.env.PUBLIC_URL + '/images/bread.png'}></img>
      <div className={'display: inline-block font-Gmarket text-ivory flex mt-5 justify-center items-center'}>
        <p className={'text-[49px] text-center'}>저희 키오스크의 메뉴판으로</p>
        <p className={'text-[49px] text-center font-semibold'}>쉽게 주문해보세요 !</p>
      </div>
      <div className={'flex justify-center'}>
        <hr className={'w-5/6 mt-5 border-ivory mb-20'}></hr>
      </div>
      <div className={'bg-cover bg-center flex'}>
        <img className={'ml-auto w-72'} src={process.env.PUBLIC_URL + '/images/deco.png'}></img>
        <img className={'ml-auto w-72 scale-x-100'} src={process.env.PUBLIC_URL + '/images/deco.png'}></img>
      </div>
      <div className={`flex justify-center mt-80 mb-80`}>
        <img src={process.env.PUBLIC_URL + '/images/logo.png'} className={`w-3/4 max-w-full`} alt="logo"/>
      </div>
      <div className={`${FlexRow} justify-center`}>
        <Fade>
            <button 
            className={`bg-yellow w-2/5 h-[200px] rounded-[30px] shadow-2xl`}
            onClick={handleOrderClick}>
                <p className={'font-Gangwon text-[100px] text-darkbrown text-center'}>주문하기</p>
            </button>
        </Fade>
      </div>
    </div>
  )
}
