import React from 'react'
import { FlexCol, PaddingX } from '../constants/style'
import { Fade, Slide } from 'react-reveal';
import { useLocation } from 'react-router-dom';

export default function Header() {
    const location = useLocation();
    const isHome = location.pathname === '/';

  return (
    <Slide top cascade>
      <div className={`${FlexCol} bg-background relative pt-[66px] pb-[150px] pl-[102px] `}>
          <img src='../images/logo.png' className={`w-[270px] h-[62px] `}/>
          {/* {!isHome && <p>여기다가 단계</p>} */}
          <p className={`abolute top-0 right-20text-white text-5xl font-Jeju`}>다음 단계</p>
      </div>
    </Slide>
  )
}
