import {React, useState, useEffect, Fragment} from 'react'
import { useLocation } from 'react-router-dom';
import { FlexRow, FlexCol} from '../constants/style';

export default function ProgressBar() {

  const location = useLocation();
  const [category, setCategory] = useState("");

  const setProgress = ['주문', '버거', '사이드', '음료', '결제'];

  const [progressBar, setProgressBar] = useState(setProgress);

  useEffect(() => {
    if (location.pathname === '/detail/burger') {
      setCategory('버거');
      setProgressBar(['주문', '버거', '결제']);
    } else if (location.pathname === '/detail/beverage') {
      setCategory('음료');
      setProgressBar(['주문', '음료', '결제']);
    } else if (location.pathname === '/detail/side') {
      setCategory('사이드');
      setProgressBar(['주문', '사이드', '결제']);
    } else if (location.pathname === '/detail/set') {
      setCategory('');
      setProgressBar(setProgress);
    }
  }, [location.pathname]);

 

  return (
    <div className={`${FlexCol} font-Jeju`}>
        <div className={`${FlexRow} ml-4 rounded-full w-[1080px] h-[18px] mt-[140px]`}>
            {/* <p className={`text-black text-4xl w-[100px]`}>주문</p>
            <p className={`w-1/4  h-[18px]`}></p>
            <p className={`text-black text-4xl w-[100px]`}>주문</p>
            <p className={`w-1/4 h-[18px]`}></p>
            <p className={`text-black text-4xl w-[100px]`}>주문</p>
            <p className={`w-1/4 h-[18px]`}></p>
            <p className={`text-black text-4xl w-[100px]`}>주문</p>
            <p className={`w-1/4 h-[18px]`}></p>
            <p className={`text-black text-4xl w-[100px]`}>주문</p> */}
            {progressBar.map((step, index) => (
                <Fragment key={index}>
                    <p className={`text-black text-4xl w-[200px]`}>{step}</p>
                    {index < progressBar.length - 1 && <p className={`w-1/${progressBar.length -1} h-[18px]`}></p>}
                </Fragment>
                )
            )}
        </div>
        <div className={`${FlexRow} ml-12 rounded-full bg-white w-[1000px] h-[18px] mt-12`}>
            <div className={`rounded-full bg-gray/50 w-[18px] h-[18px]`}></div>
            <p className={`w-1/4  h-[18px]`}></p>
            <div className={`rounded-full bg-gray/50 w-[18px] h-[18px]`}></div>
            <p className={`w-1/4 h-[18px]`}></p>
            <div className={`rounded-full bg-gray/50 w-[18px] h-[18px]`}></div>
            <p className={`w-1/4 h-[18px]`}></p>
            <div className={`rounded-full bg-gray/50 w-[18px] h-[18px]`}></div>
            <p className={`w-1/4 h-[18px]`}></p>
            <div className={`rounded-full bg-gray/50 w-[18px] h-[18px]`}></div>
        </div>
    </div>
  )
}
