import React from 'react'
import { FlexCol, PaddingX, Padding, FlexRow } from '../constants/style'
import { Fade, Slide } from 'react-reveal';
import Pulse from 'react-reveal/Pulse';
import beverageBox from '../assets/images/beverageBox.png';
import burgerBox from '../assets/images/burgerBox.png';
import setBox from '../assets/images/setBox.png';
import sideBox from '../assets/images/sideBox.png';
import { Link } from 'react-router-dom'; //이거말고
import { useNavigate } from 'react-router-dom';

export default function Home() {
  const movePage = useNavigate();

  const handleImageClick = () => {
    // 이미지 클릭 시 이동하고자 하는 페이지로 이동합니다.
    movePage('/detail');
  };

  const beverageBoxStyle = {
    width: '532px',
    height: '998px',
    //borderRadius: '45px',
    backgroundColor: 'rgba(107, 243, 0, 0.3)',
    clipPath: 'polygon(0% 80%, 0% 20%, 100% 0%, 100% 100%)',
  };



  return (
    <div className={`${FlexCol} font-Jeju bg-background relative z-0 h-screen`}>
        
        <div className={`${FlexRow} self-center`}>
            <div className={`${FlexCol}`}>
                <Fade left>
                    <div>
                        <p className="w-[503px] h-[400px] pb-12 text-[64px] flex items-center justify-end">어서오세요<br/>주문하시겠어요?</p>
                    </div>
                </Fade>
                <Fade>
                    <div onClick={handleImageClick}>
                        <img className={`w-[532px] h-[998px]`} src={burgerBox} />
                    </div>
                </Fade>
            </div>
            <div className={`${FlexCol} pl-[90px]`} >
                <Fade>
                    <img src='' useMap='#roadmap'/>
                </Fade>

                {/* <Fade>
                    <div onClick={handleImageClick}>
                        <img className={`w-[503px] h-[998px]`} src={beverageBox}/>
                    </div>
                </Fade>
                <Fade>
                    <div onClick={handleImageClick}>
                        <img className={`w-[503px] h-[612px]`} src={sideBox}/>
                    </div>
                </Fade> */}
            </div>
        </div>
        <Fade>
            <div onClick={handleImageClick} className={`justify-center self-center pt-[50px]`}>
                <img className={` h-[406px]`} src={setBox}/>
            </div>
        </Fade>
    </div>
  )
}
