import {React, useState, useEffect} from 'react'
import { useLocation } from 'react-router-dom';
import { FlexRow } from '../constants/style';

export default function ProgressBar() {

  const location = useLocation();
  const [category, setCategory] = useState("");
  const [progressBar, setProgressBar] = useState("");

  useEffect(() => {
    if (location.pathname === '/detail/burger') {
      setCategory('버거');
    } else if (location.pathname === '/detail/beverage') {
      setCategory('음료');
    } else if (location.pathname === '/detail/side') {
      setCategory('사이드');
    } else if (location.pathname === '/detail/set') {
      setCategory('');
    }
  }, [location.pathname]);


  const setProgress = ['주문', '버거', '사이드', '음료', '결제'];
  const singleProgress = ['주문', category, '결제'];

