            <div className={`rounded-full bg-gray/50 w-[18px] h-[18px]`}></div>
        </div>
        <div className={`${FlexRow} ml-12 rounded-full bg-white w-[1000px] h-[18px] `}>
            <div className={`rounded-full bg-gray/50 w-[18px] h-[18px]`}></div>
            <p className={`w-1/4  h-[18px]`}></p>
            <div className={`rounded-full bg-gray/50 w-[18px] h-[18px]`}></div>
            <p className={`w-1/4 h-[18px]`}></p>
            <div className={`rounded-full bg-gray/50 w-[18px] h-[18px]`}></div>
