import React from 'react'
import { FlexCol, PaddingX, FlexRow } from '../constants/style'
import { Fade, Slide } from 'react-reveal';
import { useLocation } from 'react-router-dom';

export default function Header() {
    const location = useLocation();
    const isHome = location.pathname === '/';
{/* {!isHome && <p>여기다가 단계</p>} */}
  return (
    <Slide top cascade>
      <div className={`${FlexCol} bg-background relative pt-[66px] pb-[150px] pl-[102px] `}>
          <div className={`${FlexRow}`}>
            <img src='../images/logo.png' className={`w-[270px] h-[62px] `}/>  
            {!isHome && 
                <button>
                    <p className={`absolute right-[90px] text-darkgreen text-5xl font-Jeju`}>다음 단계</p>
                </button>
            }               
          </div>
      </div>
    </Slide>
  )
}
