import {React, useState} from 'react'
import { FlexCol, PaddingX, Padding, FlexRow } from '../constants/style'
import { Fade, Slide } from 'react-reveal';
import Pulse from 'react-reveal/Pulse';
import { Link } from 'react-router-dom'; //이거말고
import { useNavigate } from 'react-router-dom';

export default function Home() {
  const movePage = useNavigate();
  //const [category, setCategory] = useState(['burger','set','side','beverage']);

  const handleImageClick = (category) => {
    const url = `/detail/${category}`;
    movePage(url);
  };

  const imageAreas = [
    { id: 1, shape: 'poly', coords: '0,227,500,4,502,1001,0,810', message: '영역 1을 클릭했습니다.' , category:'beverage'},
    { id: 2, shape: 'poly', coords: '501,1105,503,1498,-5,1502,5,909', message: '영역 2를 클릭했습니다.' , category:'side'},
  ];


  return (
    <div className={`${FlexCol} font-Jeju bg-background relative z-0 h-screen`}>
        
        <div className={`${FlexRow} self-center`}>
            <div className={`${FlexCol}`}>
                <Fade left>
                    <div>
                        <p className="w-[503px] h-[400px] pb-12 text-[64px] flex items-center justify-end">어서오세요<br/>주문하시겠어요?</p>
                    </div>
                </Fade>
                <Fade>
                    <div className="pt-[110px]">
                        <img onClick={()=>handleImageClick('burger')} className={`w-[532px] h-[998px] justify-bottom`} src={burgerBox} />
                    </div>
                </Fade>
            </div>
            <div className={`${FlexCol} pl-[90px]`} >
                <Fade>
                    <div>
                        <img src={beverageAndSideBox} useMap='#roadmap'/>
                    </div>
                </Fade>
            </div>
        </div>
        <Fade>
            <div className={`justify-center self-center pt-[80px]`}>
                <img onClick={()=>handleImageClick('set')}  className={` h-[406px]`} src={setBox}/>
            </div>
        </Fade>

        <map name="roadmap" id="roadmap">
        {imageAreas.map((area) => (
            <area
                key={area.id}
                shape={area.shape}
                coords={area.coords}
                onClick={() => handleImageClick(area.category)}
            />
            ))}
        </map>

    </div>
  )
}
