import {React, useState } from 'react'
import { FlexCol, PaddingX, Padding, FlexRow } from '../constants/style'
import { Fade, Slide } from 'react-reveal';

export default function Table(props) {
  const { menuList, menuCounts, updateOrder, category } = props;

  const incrementCount = (menu, detail) => {
    props.incrementCount(menu, detail);
    props.updateOrder(menu, detail, (menuCounts.find((item) => item.menu === menu && item.detail === detail)?.count || 0) + 1);
  };
  
  const decrementCount = (menu, detail) => {
    props.decrementCount(menu, detail);
    props.updateOrder(menu, detail, Math.max((menuCounts.find((item) => item.menu === menu && item.detail === detail)?.count || 0) - 1, 0));
  };


  return (
    <div className={`${FlexCol}`}>

       <div className={`bg-darkred mt-10 mb-0 p-2 
       font-Jeju text-white text-4xl text-center`}>
          <p>{props.category}</p>
       </div>

       {menuList.map((item, index) => (
        <div
          key={index}
          className={`${FlexRow} font-Jeju text-4xl bg-white p-4 mt-0 border-b-2 border-black border-l-4 border-r-4 items-center text-center`}
        >
          <div className={`w-[40%] m-0 p-0`}>
            <p>{item.menu}</p>
            <p className={`text-2xl`}>{item.detail}</p>
          </div>
          <div className={`w-[30%] m-0 p-0`}>
            <p>{item.price}Ïõê</p>
          </div>
          <div
            className={`w-[30%] m-0 p-0 ${FlexRow} justify-center gap-2 `}
          >
            <button
              className={`${
                menuCounts[item.menu] > 0 ? 'bg-red' : 'bg-gray-400'
              } rounded-full w-[50px] h-[50px] text-white`}
              onClick={() => decrementCount(item.menu)}
            >
              <p className={`mt-1`}>-</p>
            </button>
            <div
              className={`rounded-md border-2 border-black w-[80px] h-[50px] text-center`}
            >
              <p className={`mt-1`}>{menuCounts[item.menu] || 0}</p>
            </div>
            <button
              className={`${
                menuCounts[item.menu] > 0 ? 'bg-red' : 'bg-gray-400'
              } rounded-full w-[50px] h-[50px] text-white`}
              onClick={() => incrementCount(item.menu)}
            >
              <p className={`mt-1`}>+</p>
            </button>
          </div>
        </div>
      ))}
       
    </div>
  )
}
