import {React, useState, useEffect} from 'react'
import { useLocation } from 'react-router-dom';
import { FlexRow, FlexCol} from '../constants/style';

export default function ProgressBar() {

  const location = useLocation();
  const [category, setCategory] = useState("");

  const setProgress = ['주문', '버거', '사이드', '음료', '결제'];

  const [progressBar, setProgressBar] = useState("");

  useEffect(() => {
    if (location.pathname === '/detail/burger') {
      setCategory('버거');
      setProgressBar(singleProgress);
    } else if (location.pathname === '/detail/beverage') {
      setCategory('음료');
      setProgressBar(singleProgress);
    } else if (location.pathname === '/detail/side') {
      setCategory('사이드');
      setProgressBar(singleProgress);
    } else if (location.pathname === '/detail/set') {
      setCategory('');
      setProgressBar(setProgress);
    }
  }, [location.pathname]);

  
  const singleProgress = ['주문', category, '결제'];

 

  return (
    <div className={`${FlexCol} font-Jeju`}>
        <div className={`${FlexRow} ml-4 rounded-full w-[1060px] h-[18px] mt-[140px]`}>
            {/* <p className={`text-black text-4xl w-[100px]`}>주문</p>
            <p className={`w-1/4  h-[18px]`}></p>
            <p className={`text-black text-4xl w-[100px]`}>주문</p>
            <p className={`w-1/4 h-[18px]`}></p>
            <p className={`text-black text-4xl w-[100px]`}>주문</p>
            <p className={`w-1/4 h-[18px]`}></p>
            <p className={`text-black text-4xl w-[100px]`}>주문</p>
            <p className={`w-1/4 h-[18px]`}></p>
            <p className={`text-black text-4xl w-[100px]`}>주문</p> */}
            {progressBar.map((step, index) => (
                <React.Fragment key={index}>
                    <p className={`text-black text-4xl w-[100px]`}>{step}</p>
                    {index < progressBar.length - 1 && <p className={`w-1/4  h-[18px]`}></p>}
                </React.Fragment>
                ))}
        </div>
        <div className={`${FlexRow} ml-12 rounded-full bg-white w-[1000px] h-[18px] mt-12`}>
            <div className={`rounded-full bg-gray/50 w-[18px] h-[18px]`}></div>
            <p className={`w-1/4  h-[18px]`}></p>
            <div className={`rounded-full bg-gray/50 w-[18px] h-[18px]`}></div>
            <p className={`w-1/4 h-[18px]`}></p>
            <div className={`rounded-full bg-gray/50 w-[18px] h-[18px]`}></div>
            <p className={`w-1/4 h-[18px]`}></p>
            <div className={`rounded-full bg-gray/50 w-[18px] h-[18px]`}></div>
            <p className={`w-1/4 h-[18px]`}></p>
            <div className={`rounded-full bg-gray/50 w-[18px] h-[18px]`}></div>
        </div>
    </div>
  )
}
