import {React, useState } from 'react'
import { FlexCol, PaddingX, Padding, FlexRow } from '../constants/style'
import { Fade, Slide } from 'react-reveal';

export default function Table(props) {
  const { menuList, updateOrder, orderedItems } = props;

  const getMenuCount = (menu) => {
    const foundItem = orderedItems.find(
      (item) => item.menu === menu.menu && item.detail === menu.detail
    );
    return foundItem ? foundItem.count : 0;
  };

  const incrementCount = (menu) => {
    const updatedItems = [...orderedItems];
    const index = updatedItems.findIndex(
      (item) => item.menu === menu.menu && item.detail === menu.detail
    );

    if (index !== -1) {
      updatedItems[index].count++;
    } else {
      updatedItems.push({ ...menu, count: 1 });
    }

    updateOrder(menu, getMenuCount(menu) + 1, menu.detail);
  };

  const decrementCount = (menu) => {
    const updatedItems = [...orderedItems];
    const index = updatedItems.findIndex(
      (item) => item.menu === menu.menu && item.detail === menu.detail
    );

    if (index !== -1) {
      if (updatedItems[index].count > 0) {
        updatedItems[index].count--;
        updateOrder(menu, getMenuCount(menu) - 1, menu.detail);
      }
    }
  };


  return (
    <div className={`${FlexCol}`}>

       <div className={`bg-darkred mt-10 mb-0 p-2 
       font-Jeju text-white text-4xl text-center`}>
          <p>{props.category}</p>
       </div>
    
      {menuList.map((item, index) => (
        <div key={index} className={`${FlexRow} font-Jeju text-4xl bg-white 
        p-4 mt-0 border-b-2 border-black border-l-4 border-r-4 items-center text-center`}>
          <div className={`w-[40%] m-0 p-0`}>
            <p>{item.menu}</p>
            <p className={`text-2xl`}>{item.detail}</p>
          </div>
          <div className={`w-[30%] m-0 p-0`}>
            <p>{item.price}Ïõê</p>
          </div>
          <div className={`w-[30%] m-0 p-0 ${FlexRow} justify-center gap-2 `}>
            <button className={`${getMenuCount(item)>0? 'bg-red' : 'bg-gray-400'} rounded-full w-[50px] h-[50px] text-white`}
            onClick = {()=>decrementCount(item)}>
              <p className={`mt-1`}>-</p>
            </button>
            <div className={`rounded-md border-2 border-black w-[80px] h-[50px] text-center`}>
              <p className={`mt-1`}>{getMenuCount(item)}</p>
            </div>
            <button className={`${getMenuCount(item)>0? 'bg-red' : 'bg-gray-400'} rounded-full w-[50px] h-[50px] text-white`}
             onClick={() => incrementCount(item)}>
              <p className={`mt-1`}>+</p>
            </button>
          </div>
        </div>
      ))}
       
    </div>
  )
}
